DELIMITER ;;
CREATE TRIGGER `goodTrigger_insert` AFTER INSERT ON `goodcritic` FOR EACH ROW BEGIN
  DECLARE good int DEFAULT 0;
  SELECT good into good from publishcritic where id=new.pid;
  update publishcritic set good=good+1 where id=new.pid;
end
;;
DELIMITER ;
DROP TRIGGER IF EXISTS `goodTrigger_delete`;
DELIMITER ;;
CREATE TRIGGER `goodTrigger_delete` AFTER DELETE ON `goodcritic` FOR EACH ROW BEGIN
  SELECT good into @good from publishcritic where id=old.pid;
  update publishcritic set good=@good-1 where id=old.pid;
end
;;
DELIMITER ;
